import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

function CardItem(props) {
  return (
    <div className="card_item">
      <h2>{props.item.nome}</h2>
      <img src={props.item.imagem} alt="Imagem da Personagem" width="300" />
    </div>
  );
}

class ListarItens extends React.Component {
  //definir estado inicial
  constructor(props) {
    super(props);

    this.state = {
      itens: [],
    };
  }

  async componentDidMount() {
    console.log("Componente ListarItens construído");

    const request = await fetch("https://backend-flexivel.herokuapp.com/", {
      headers: new Headers({
        Authorization: "luiz.cordeiro",
      }),
    });

    const json = await request.json();

    // Atualiza o estado
    this.setState({
      itens: json,
    });

    console.log(json);
  }

  render() {
    return (
      <div className="lista_itens">
        {itens.map((item, index) => (
          <CardItem item={item} key={index} />
        ))}
      </div>
    );
  }
}

function App() {
  return <ListarItens />;
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById("root")
);
